    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String nome = jTextField1.getText();
         int distancia = Integer.valueOf(jTextField2.getText());
         int consumo = Integer.valueOf(jTextField3.getText());
         int preco = Integer.valueOf(jTextField4.getText());
         int gasto;
         
         gasto=(distancia/consumo)*preco;
         
         jLabel8.setText(nome+" teve "+gasto+" R$ de gasto");
         
         
           
        
    }//GEN-LAST:event_jButton1ActionPerformed

